let poData = `*:/Sysdb/lag/input/interface/lag/intfStatus/*`
let poPhyData = `*:/Sysdb/lag/input/config/cli/phyIntf/*`
let poMemberData = `*:/Sysdb/lag/input/interface/lag/intfStatus/*/member` | recmap(2, merge(_value))
let workingData = newDict()

# Get the status of all port-channels
for device, devData in poData {
    if dictHasKey(_Tags, device) {
        workingData[device] = newDict()
        for int,intData in devData {
            let data = merge(intData)
            workingData[device][int] = newDict()
            workingData[device][int]["operStatus"] = data["operStatus"]["Name"]
        }
    }
}
# Find all the interfaces configured to be in a port-channel
for device, devData in poPhyData {
    if dictHasKey(_Tags, device) {
        if !dictHasKey(workingData, device) {
            workingData[device] = newDict()
        }
        for int,intData in devData {
            let intData = merge(intData)
            # Convert to string, find the start (index) of the port-channel name and then isolate the name 
            let lagStr = str(intData["lag"])
            let lagIntLocation = strIndex(lagStr, "Port-Channel")
            let lag = strCut(lagStr, lagIntLocation, -1)
            if !dictHasKey(workingData[device], lag) {
                workingData[device][lag] = newDict()
            }
            workingData[device][lag][int] = false
        }
    }
}
# Change false to true for physical interfaces that are members of a port-channel.
# The falses that remain are interfaces configured to be in a port-channel but are not.
for device, devData in poMemberData {
    if dictHasKey(_Tags, device) {
        if !dictHasKey(workingData, device) {
            workingData[device] = newDict()
        }
        for lag, lagVal in devData {
            if !dictHasKey(workingData[device], lag) {
                workingData[device][lag] = newDict()
            }
            for int, intVal in lagVal {
                workingData[device][lag][int] = true
            }
        }
    }
}
# Report which interfaces are not up in the port-channel
for device, devData in workingData {
    if dictHasKey(_Tags, device) {
        if !dictHasKey(output, device) {
            output[device] = newDict()
        }
        let poInterfaceList = ""
        let phyInterfaceList = ""
        # Find all the port-channels that are down
        for interface, interfaceData in devData {
            if interfaceData["operStatus"] != "intfOperUp" {
                if poInterfaceList == "" {
                    let poInterfaceList = interface
                } else {
                    let poInterfaceList = poInterfaceList + ", " + interface
                }
            }
            # Find all the member interfaces that are down. The port-channel could be up and just missing a member.
            for phyInt, phyIntVal in interfaceData {
                if strHasPrefix(phyInt, "Ethernet") {
                    if phyIntVal == false {
                        if phyInterfaceList == "" {
                            let phyInterfaceList = phyInt
                        } else {
                            let phyInterfaceList = phyInterfaceList + ", " + phyInt
                        }
                    }
                }
            }
        }
        # Add port-channels to output
        if poInterfaceList == "" {
            output[device]["Port-Channel Down"] = "All up"
        } else {
            output[device]["Port-Channel Down"] = poInterfaceList
        }
        # Add physical interfaces to output
        if phyInterfaceList == "" {
            output[device]["Port-Channel Member Down"] = "All up"
        } else {
            output[device]["Port-Channel Member Down"] = phyInterfaceList
        }
    }
}
